mapper.select:
select userPassword
from user
where userId = #{userId}

select userId, userNickName, userHead
from user
where userId = #{userId}

select count(id) from user
and userId = #{userId}
and userNickName like concat('%', #{userNickName}, '%')

select userId, userNickName, userHead
from user


        and userId = #{userId}


        and userNickName like concat('%', #{userNickName}, '%')


order by id desc
limit #{pageStart}, #{pageEnd}
